mapper.insert:
insert into p_management
set iuser=#{iuser}
,year = year(current_timestamp())
,month = month(current_timestamp())
,month_limit=#{monthLimit}
,balance=#{monthLimit}

mapper.select:
SELECT B.menu,sum(A.current_menu_price) sum
FROM p_payment A
INNER JOIN  (
SELECT iuser_menu,menu
FROM p_user_menu
WHERE iuser=#{iuser}
UNION
SELECT  icommon_menu, menu
FROM p_common_menu
)B
ON A.imenu=B.iuser_menu
WHERE month(A.payment_at)=#{month} and year(A.payment_at)= #{year}
GROUP BY B.menu;

mapper.update:
update  p_management
set
    month_limit= month_limit + #{monthLimit}
,balance= balance + #{monthLimit}
where iuser=#{iuser} and year=year(current_timestamp()) and month = month(current_timestamp())

update p_management
set balance=balance-#{cruuntMenuPrice}
,expense=expense+#{cruuntMenuPrice}
where iuser=#{iuser} and year=#{year} and month=#{month}
