mapper.select:
SELECT A.ipayment,A.imenu,A.payment_at paymentAt
,A.current_menu_price currentMenuPrice,A.review_grade reviewGrade,A.restaurant
FROM p_payment A
    INNER JOIN (
    SELECT X.imanagement
    FROM p_management X
    INNER JOIN p_user Y
    ON X.iuser=Y.iuser
    WHERE X.iuser=#{iuser}
) B
ON A.imanagement = B.imanagement
WHERE DATE(A.payment_at)=#{paymentAt}
;
